import React, { useState, useEffect } from "react"
import { Media, Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

const CommentIndex = (props) => {
  const [commentEntry, setCommentEntry]= useState("")
  const [commentEditEntry, setCommentEditEntry] = useState("")
  // const [showEdit, setShowEdit] = useState(false)
  const [editArray, setEditArray] = useState([])
  const [comments, setComments]=useState([])
  const [success, setSuccess ]=useState(false)
  const [error, setError ]=useState(false)

  useEffect(() => {
    getComments()},[])

  const handleChange = (e) =>{
    setCommentEntry(e.target.value)
  }
  const handleSubmit = (e) =>{
    e.preventDefault()
    addToComments()
  }

  const handleChangeEdit = e => {
    e.preventDefault()
    setCommentEditEntry(e.target.value)
  }

  const addToComments = () => {
    fetch("/comments", {
      body: JSON.stringify({post: commentEntry, trail_id: props.match.params.id}),
      headers:{
        "Content-Type": "application/json"
      },
      method: "POST"
    })
    .then(response => {
      if (response.ok) setSuccess(true)
      else setError(true)
    })
    .then(()=>{
      getComments()
    })
    .catch(error => {
      console.log("error:",error)
    })
  }
  //fetch comments
  async function getComments() {
    try {
      //GET data from the backend
      let response = await fetch('/comments')

        let data = await response.json();
        //all good?
        if(response.status === 200) {
          //check the console to make sure we have all the trails
          console.log("data", data)
          let sortedData = data.filter(trail => {
            return trail.trail_id == props.match.params.id
          })
          //populate the newTrails state array with data
          setComments(sortedData)
          console.log("sort", sortedData)
        }
      } catch (err) {
        console.log(err)
      }
    }
  const handleDelete = (e) => {
    deleteComment(e)
  }
  const deleteComment = (id) => {
    fetch(`/comments/${id}`, {
      headers:{
        "Content-Type": "application/json"
      },
      method: "DELETE"
    })
    .then(response => {
      if (response.ok) {
        // If favorite delete request is successful, set favorited to false
        setSuccess(true)
      }
    })
    .then(() => {
      // Refresh the API call after delete action
      getComments()
    })
  }

  const handleEditClick = (id) => {
    if (!editArray.includes(id)) {
      setEditArray([...editArray, id])
    } else {
      setEditArray([...editArray.splice(editArray.indexOf(id),1)])
    }
  }

  const handleUpdate = e => {
    e.preventDefault
    updateComment()
  }

  const updateComment = () => {

  }

  return ( 

  <>
      <FormGroup>
        <Label htmlFor="commentEntry">Add Comment</Label>
        <Input type="textarea" name="commentEntry" id="commentEntry" value={ commentEntry } onChange={ handleChange }/>
      </FormGroup>
      <Button onClick={ handleSubmit }>Submit</Button>
    { comments.map((comment, index)=> {
      let editable = false
        if(props.user_id === comment.user_id) {
          editable = true
        }
        let date = comment.updated_at.substring(0,10)
        let showEdit = editArray.includes(comment.id)
      return(
        <>
          {showEdit &&
            <FormGroup>
              <Label htmlFor="commentEditEntry">Edit Comment</Label>
              <Input type="textarea" name="commentEditEntry" id="commentEditEntry" value={ commentEditEntry } onChange={ handleChangeEdit }/>
              <Button onClick={ () => handleUpdate(comment.id) }>Update</Button>
            </FormGroup>
          }

          <Media key = { index }>
            <Media left href="#">
              <Media object data-src='#' alt="" />
            </Media>
            <Media body>
              { comment.post }
            </Media>
            <Media body>
              { date }
            </Media>
            { editable &&
              <>
                <Button onClick={ () => handleEditClick(comment.id) }>Edit</Button>
                <Button onClick={ () => handleDelete(comment.id) }>Delete</Button>
              </>
            }
          </Media>
        </>
      )
    })}
  </>
  );
};

export default CommentIndex
