import React, { useState, useEffect } from "react"
import { Button, Form, FormGroup, Label, Input, Container } from 'reactstrap'


const TrailsIndex = () => {

  const [newTrails, setNewTrails] = useState([])
  const [newForm, setNewForm] = useState({
    latitude: "",
    longitude: ""
  })
  useEffect(() => {
    getTrails()},[])

    const handleChange = (e) =>{
      setNewForm({
        ...newForm, 
        [e.target.name]: e.target.value
      })
    }
    async function getTrails() {
      try {
        //GET data from the backend
        let response = await fetch(`https://www.hikingproject.com/data/get-trails?lat=${ latitude }&lon=${ longitude }&maxDistance=10&key=200805451-d58078a69001bb6f37cb92b68bbebae3`)
          let data = await response.json();
          //all good?
          if(response.status === 200) {
            //check the console to make sure we have all the cats
            console.log("data", data)
            //populate the newCats state array with data
            setNewTrails(data)
          }
        } catch (err) {
          console.log(err)
        }
      }

      return (
        <>
        <h2>All of the fancy trails</h2>
        <Container>

          <Form>
            <FormGroup>
              <Label htmlFor="latitude">Latitude</Label>

              <Input type="text" name="latitude" value={ form.latitude } onChange={ handleChange } placeholder="" />

              <Label htmlFor="longitude">Longitude</Label>

              <Input type="text" name="longitude" value={ form.longitude } onChange={ handleChange } placeholder="" />
            </FormGroup>
          </Form>
        </Container>
        </>
      );
    }

    export default TrailsIndex
