const Badges = props => {
  useEffect(() => {
    getBadges()
  })

  async function getBadges() {
    try {
      let response = await fetch('/users/comments')
      let data = await response.json()
      if (response.ok) {
        console.log("badges", data)
      }
    } catch (err) {
      console.log(err)
    }
  }

  // const commentCount = () => {
