import React, { useState, useEffect } from "react"


const TrailsProfile = (props) => {
 const [currentTrail, setCurrentTrail] = useState([])
//  const { id } = props.match.params
useEffect(() => {
  getTrail()},[])

  async function getTrail() {
    try {
      //GET data from the backend
      let response = await fetch(`https://www.hikingproject.com/data/get-trails-by-id?ids=7022523&key=200805451-d58078a69001bb6f37cb92b68bbebae3`)
        let data = await response.json();
        //all good?
        if(response.status === 200) {
          //check the console to make sure we have all the trails
          console.log("data", data.trails)
          //populate the newTrails state array with data
          setNewTrails(data.trails)
        }
      } catch (err) {
        console.log(err)
      }
    }
    return (
      <>
      <h2>What a fancy trail profile</h2>
      <h3> { currentTrail.name }</h3>
      </>
    );
}

export default TrailsProfile
