import React, { useState, useEffect } from "react"



const TrailsIndex = () => {

  const [newTrails, setNewTrails] = useState([])

  useEffect(() => {
    getTrails()},[])

 async function getTrails() {
   try {
      //GET data from the backend
      let response = await fetch("https://www.hikingproject.com/data/get-trails?lat=32.7157&lon=-117.1611&maxDistance=10&key=200805451-d58078a69001bb6f37cb92b68bbebae3")
      let data = await response.json();
      //all good?
      if(response.status === 200) {
        //check the console to make sure we have all the cats
        console.log("data", data)
        //populate the newCats state array with data
        setNewTrails(data)
      }
    } catch (err) {
        console.log(err)
    }
 }

    return (
      <>
      <h2>All of the fancy trails</h2>

      </>
    );
}

export default TrailsIndex
