import React, { useState, useEffect } from 'react'

const Badges = props => {
  const [badges, setBadges] = useState([])

  useEffect(() => {
    getBadges()
  },[])

  async function getBadges() {
    try {
      let response = await fetch('/badges')
      let data = await response.json()
      if (response.ok) {
        console.log("badges", data)
        setBadges(data)
      }
    } catch (err) {
      console.log(err)
    }
  }

  // const commentCount = () => {
  //   if 
  // }

  return(
    <>
      <h6>Your Badges</h6>
      <ul>
        {badges.map((badge,index) => {
          return(
            <li>{badge.level}</li>
          )
        })}
      </ul>
    </>
  )
}

export default Badges
